import traceback

from playwright.sync_api import Page, Request, Route


def test_cache_requests(page: Page) -> None:
    def handle(route: Route, request: Request) -> None:
        # If response exists in cache
        if request.url in responses:
            cached_res = responses[request.url]
            # Serve cached response
            route.fulfill(
                body=cached_res["body"], headers=cached_res["headers"]
            )
        else:
            route.continue_()
            try:
                # Get response and store it in responses dictionary by URL
                response = route.request.response()
                responses[request.url] = {
                    "body": response.body().decode("utf-8"),
                    "headers": route.request.headers,
                }
            except Exception:
                traceback.print_exc()

    def save_responses_to_file() -> None:
        # Create directory if not exists
        os.makedirs(os.path.dirname(cache_file_path), exist_ok=True)
        # Write cache to file
        with open(cache_file_path, "w") as file:
            json.dump(responses, file)

    def load_cache_from_file() -> dict:
        # Load cached responses from cache file
        if os.path.exists(cache_file_path):
            with open(cache_file_path, "r") as file:
                return json.load(file)
        return {}

    # A dictionary to store cached responses
    responses = {}
    cache_file_path = os.path.join(
        os.path.dirname(__file__), "responses", "responses.json"
    )

    responses = load_cache_from_file()

    # Intercept all requests
    page.route("**/*", handle)

    page.goto("https://playwright.dev/")

